#!/usr/bin/env node

var jws = require('jws')
  , fs = require('fs');


var payload = { iss: 'https://op.example.com/',
    sub: 'mailto:bob@example.com',
    aud: 'https://rp.example.com/',
    exp: 7702588800,
    scope: { 'foo': 'bar' } };

var data = jws.sign({
  header: { alg: 'RS256' },
  payload: payload,
  privateKey: fs.readFileSync('test/keys/rsa/private-key.pem')
});

console.log(data);